CREATE TABLE REAL_TIME_MONITORING 
    ( 
     REPORT TEXT 
    ) TABLESPACE USERS 
    NOLOGGING 
    LOB ( REPORT ) STORE AS LS1 
        ( 
        TABLESPACE USERS 
        STORAGE ( 
            PCTINCREASE 0 
            MINEXTENTS 1 
            MAXEXTENTS UNLIMITED 
            FREELISTS 1 
            BUFFER_POOL DEFAULT 
        ) 
        CHUNK 8192 
        RETENTION 
        ENABLE STORAGE IN ROW
        NOCACHE NOLOGGING 
    ) 
;





